(16, 'feature_of_features', '[{\"feature_logo\":\"1690960676-feature_logo.png\",\"feature_heading\":\"Free & Open Source\",\"feature_description\":\"<p>Give your customers a quick way to find the products they want. Thanks to a built-in, intuitive search and filtering, they can view only those products that they&rsquo;re interested in.<\\/p>\",\"feature_more_details_link\":\"#\",\"feature_more_details_button_text\":\"Find Out More\"},{\"feature_logo\":\"1690960805-feature_logo.png\",\"feature_heading\":\"Guaranteed to Grow Your Business\",\"feature_description\":\"<p>Give your customers a quick way to find the products they want. Thanks to a built-in, intuitive search and filtering, they can view only those products that they\\u2019re interested in.<\\/p>\",\"feature_more_details_link\":\"#\",\"feature_more_details_button_text\":\"Find Out More\"},{\"feature_logo\":\"1690960837-feature_logo.png\",\"feature_heading\":\"24\\/7 Support\",\"feature_description\":\"<p>Give your customers a quick way to find the products they want. Thanks to a built-in, intuitive search and filtering, they can view only those products that they&rsquo;re interested in.<\\/p>\",\"feature_more_details_link\":\"#\",\"feature_more_details_button_text\":\"Find Out More\"}]', '2024-06-24 19:15:50', '2024-06-24 19:15:50'),
